INSERT INTO `im_goods`(`id`, `name`, `price`, `pic`, `des`, `content`, `user_id`, `create_time`, `update_time`, `view`) VALUES (2, '沐浴按摩奶膏奶浴五大连池AD火山泥体膜按摩膏', 7, 'http://localhost/mall/static/file/2017/1115/5a0bbee9221279437.jpg', 'AD火山泥体膜按摩膏 80克\r\n五大连池原生态天然火山泥\r\n滋润保湿紧致抗敏抗衰', '<p style=\"color:#696969;font-family:arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-size:16px;\"><span style=\"font-family:kaiti_gb2312;font-size:16px;\"><span style=\"background-color:#FFD966;font-size:16px;\">1.火山泥冷泉净白润体膜中微细粒子能彻底清除毛孔深处的污垢和油脂，打开毛孔增加透氧性，有助于细胞吸收，把多余的水分排出体外，</span></span></span></span>\r\n</p>\r\n<span style=\"font-family:arial;background-color:#FFFFFF;color:#CC0000;\"><span style=\"background-color:#FFD966;\"></span></span>\r\n<p style=\"color:#696969;font-family:arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-size:16px;\"><span style=\"font-family:kaiti_gb2312;font-size:16px;\"><span style=\"background-color:#FFD966;font-size:16px;\">2.多种矿物质促进细胞活化功能，皮肤会选择性的把矿物储存起来，以待补充增强均衡皮肤的成分，对抗氧化及自由基，减少色斑和黑色素的形成，矿物质化化合空气中水分，起到保湿滋润功效。</span></span></span></span>\r\n</p>\r\n<span style=\"font-family:arial;background-color:#FFFFFF;color:#CC0000;\"><span style=\"background-color:#FFD966;\"></span></span>\r\n<p style=\"color:#696969;font-family:arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-size:16px;\"><span style=\"font-family:kaiti_gb2312;font-size:16px;\"><span style=\"background-color:#FFD966;font-size:16px;\">3.多种矿物离子强渗透性，促进离子交换，有效舒缓肌肤的紧张疲劳，有醒肤镇定的效果，调节皮肤组织成分均衡，维持酸碱平衡度，对抗紫外线A和B，</span></span></span></span>\r\n</p>\r\n<span style=\"font-family:arial;background-color:#FFFFFF;color:#CC0000;\"><span style=\"background-color:#FFD966;\"></span></span>\r\n<p style=\"color:#696969;font-family:arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-size:16px;\"><span style=\"font-family:kaiti_gb2312;font-size:16px;\"><span style=\"background-color:#FFD966;font-size:16px;\">精粹成分&nbsp;</span></span></span></span>\r\n</p>\r\n<span style=\"font-family:arial;background-color:#FFFFFF;color:#CC0000;\"><span style=\"background-color:#FFD966;\"></span></span>\r\n<p style=\"color:#696969;font-family:arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-size:16px;\"><span style=\"font-family:kaiti_gb2312;font-size:16px;\"><span style=\"background-color:#FFD966;font-size:16px;\">火山泥，火山矿泉水，单硬脂酸甘油酯，金缕梅蒸馏液，聚氧乙烯硬脂酸酯，琼腊硬酯醇等。</span></span></span></span>\r\n</p>\r\n<span style=\"font-family:arial;background-color:#FFFFFF;color:#CC0000;\"><span style=\"background-color:#FFD966;\"></span></span>\r\n<p style=\"color:#696969;font-family:arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-size:16px;\"><span style=\"font-family:kaiti_gb2312;font-size:16px;\"><span style=\"background-color:#FFD966;font-size:16px;\">使用方法；</span></span></span></span>\r\n</p>\r\n<span style=\"font-family:arial;background-color:#FFFFFF;color:#CC0000;\"><span style=\"background-color:#FFD966;\"></span></span>\r\n<p style=\"color:#696969;font-family:arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-size:16px;\"><span style=\"font-family:kaiti_gb2312;font-size:16px;\"><span style=\"background-color:#FFD966;font-size:16px;\">沐浴擦干身体后，将按摩膏均匀涂抹于身体上，并配以按摩至吸收20-30分钟，最好配合桑拿5-10分钟效果更佳，用清水洗净即可（每隔2-3天做一次，效果更佳）</span></span></span></span>\r\n</p>', 1, 1510719209, 1510719209, 40);
INSERT INTO `im_goods`(`id`, `name`, `price`, `pic`, `des`, `content`, `user_id`, `create_time`, `update_time`, `view`) VALUES (3, '左岸春天芦荟搓泥宝洁身美肤宝精华露', 2, 'http://localhost/mall/static/file/2017/1115/5a0bf0fb7579c7896.jpg', '左岸春天芦荟搓泥洁身美肤宝80克 皮肤橡皮擦清洁保湿', '<p style=\"text-align:left;font-family:arial;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-size:14px;\"><span style=\"font-family:kaiti_gb2312;font-size:14px;\"><span style=\"background-color:#FFD966;font-size:14px;\">搓泥宝用法&nbsp;&nbsp;</span></span></span></span>\r\n</p>\r\n<p style=\"font-family:arial;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<span style=\"color:#CC0000;\"></span>\r\n</p>\r\n<p style=\"text-align:left;font-family:arial;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-size:14px;\"><span style=\"background-color:#FFD966;font-size:14px;\">使用前不用使用沐浴露，先用水冲洗下身体</span></span></span>\r\n</p>\r\n<p style=\"text-align:left;font-family:arial;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-size:14px;\"><span style=\"background-color:#FFD966;font-size:14px;\">然后把搓泥宝涂抹全身</span></span></span>\r\n</p>\r\n<p style=\"text-align:left;font-family:arial;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-size:14px;\"><span style=\"background-color:#FFD966;font-size:14px;\">1-2分钟之后稍微冲洗下</span></span></span>\r\n</p>\r\n<p style=\"text-align:left;font-family:arial;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-size:14px;\"><span style=\"background-color:#FFD966;font-size:14px;\">这时候搓泥里面的精华会附着在身体的脏东西上面，然后用搓澡巾搓洗就可以</span></span></span>\r\n</p>\r\n<p style=\"text-align:left;font-family:arial;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-size:14px;\"><span style=\"font-family:kaiti_gb2312;font-size:14px;\"><span style=\"background-color:#FFD966;font-size:14px;\">冲洗身体后涂抹全身然后用搓澡巾在搓洗，去油灰效果特别的好</span></span></span></span>\r\n</p>', 1, 1510732027, 1510732027, 3);
INSERT INTO `im_goods`(`id`, `name`, `price`, `pic`, `des`, `content`, `user_id`, `create_time`, `update_time`, `view`) VALUES (4, '浴之轩三合一雪蚕丝蛋白乳液', 3, 'http://localhost/mall/static/file/2017/1115/5a0bf28ae1a757217.jpg', '滋润肌肤，营养肌肤。', '<p>\r\n	<strong>功效：滋润肌肤，营养肌肤。持久保湿，令肌肤柔软细腻，增加皮肤角质层的含水量，促进色素分解，均匀肌肤。长期使用可使皮肤柔软，细腻富有光泽和弹性。<br />\r\n</strong> \r\n</p>\r\n<p>\r\n	<strong>使用方法：沐浴时，将雪蚕丝蛋白，百花蜂蜜调和，均匀涂在皮肤上。轻揉按摩5-10分钟。待其营养肌肤吸收后。把金玫瑰精油均匀涂于肌肤上，再进行按摩5-10分用清水洗净。</strong> \r\n</p>', 1, 1510732426, 1510732426, 11);
