INSERT INTO `im_goods`(`id`, `name`, `price`, `pic`, `des`, `content`, `user_id`, `create_time`, `update_time`, `view`) VALUES (6, '圣泊兰蜗牛原液美肤套装五件套', 13, 'http://localhost/mall/static/file/2017/1116/5a0cdb3c10da57188.jpg', '专为寒冷干燥季节设计 滋润保湿', '<span style=\"font-family:kaiti_gb2312;font-size:18px;background-color:#FFD966;\">将蜗牛原液玻尿酸，海盐去角质磨砂乳，罗马洋甘菊精油，骨胶原补水润肤身体乳调和在一起，均系涂抹于全身，轻柔按摩5-10分钟，使营养成分充分吸收，在以清水冲净即可</span>', 1, 1510791996, 1510791996, 1);
